import { useState } from "react";

const tableData = [
    { id:"1", date:"1-03-2023", amount:"56"},
    { id:"2", date: "2-03-2023" , amount:"76"},
    { id:"3", date: "3-03-2023",  amount:"84"},
    { id:"4", date: "4-03-2023",amount:"65"},
    { id:"5", date: "5-03-2023",amount:"89"},
  ];


function App() {
  const [date , setDate] = useState();
  const [data, setData] = useState(tableData);

  const filterdatehandler = (event) =>{
        setDate(event.target.value);
  }

  const handleClick = () =>{
    const sort =  tableData.sort((a ,b)=>{
       return  b.amount - a.amount
    }).map(data => {
           return data;
     })
     setData(sort);
  }

  const  handlesubmitfilter = () =>{
    const finaloutput = tableData.filter( finaldate =>{
            const myarray = date.split("-");
            let year  = myarray[0]
            let month = myarray[1]
            let day   = myarray[2]
            const finaldateformat = day+"-"+month+"-"+year;
            return finaldate.date === finaldateformat;
    });

     setData(finaloutput);
  }



  return (
    <div>
      <h1>Table</h1>
      <center>
        <input type="date" name="date" id="date" onChange={filterdatehandler} pattern="\d{1,2}/\d{1,2}/\d{4}" />
        &nbsp;
        <button name="filter" id="filter" onClick={handlesubmitfilter}>
          filter
        </button>
      </center>
      <br />
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Description</th>
            <th>Name</th>
            <th><button onClick={handleClick}>Amount</button></th>
          </tr>
        </thead>
        <tbody>
          {
          allvalue.map((datas,index) => {
            return (
              <tr key={index}>
                <td>{datas.date}</td>
                <td>{datas.description}</td>
                <td>{datas.name}</td>
                <td>{datas.amount}</td>
              </tr>
            );
          })
        }
        </tbody>
      </table>
    </div>
  );
}

export default App;
